import type {CommonOptions} from '../arguments/options';
import type {Intersects} from '../utils';
import type {StdioSingleOptionItems, InputStdioOption} from './type';
import type {FdStdioArrayOption} from './option';

// Whether `result.stdio[FdNumber]` is an input stream
export type IsInputFd<
	FdNumber extends string,
	OptionsType extends CommonOptions,
> = FdNumber extends '0'
	? true
	: Intersects<StdioSingleOptionItems<FdStdioArrayOption<FdNumber, OptionsType>>, InputStdioOption>;
